'create or replace function TRS_TRANSLATE_STRING_GOOGLE',
'( IV_TEXT in nclob',
', IV_SOURCE_LANGUAGE_CODE in varchar2',
', IV_TARGET_LANGUAGE_CODE in varchar2',
') return nclob',
'is',
'    LV_RESPONSE         apex_json.t_values;',
'    LV_UTL              varchar2(32767) default ''http://translate.googleapis.com/translate_a/single?client=gtx&sl='' ||IV_SOURCE_LANGUAGE_CODE || ''&tl='' || IV_TARGET_LANGUAGE_CODE || ''&dt=t&q='';',
'    LV_OUTPUT           varchar2(32767) default '''';',
'    LV_CLEAN_TEXT       varchar2(32767) default '''';    ',
'    LV_OUTPUT_ARR       UTL_HTTP.html_pieces;',
'    LV_COUNTER          number          default 1;',
'    LV_START_CLEAN      number;',
'    LV_END_CLEAN        number;',
'    LV_LENGTH_CLEAN     number;',
'begin',
'',
'  -- Create URL including escaped text',
'  LV_UTL:=LV_UTL||utl_url.escape(IV_TEXT,TRUE);',
'',
'  -- Request result in pieces if over 2000 characters (max 16 since limit is varchar2(32767)',
'  LV_OUTPUT_ARR := utl_http.request_pieces',
'    (   url => LV_UTL,',
'        max_pieces => 16',
'    );',
'',
'  -- Concat result into total result',
'  FOR i IN 1 .. LV_OUTPUT_ARR.COUNT',
'  LOOP',
'     LV_OUTPUT := LV_OUTPUT || LV_OUTPUT_ARR(i);',
'  END LOOP;',
'',
'  -- Get the translated text which is the first text between square brackets quotes per block, block can occur multiple times.',
'  while instr(LV_OUTPUT,''["'',1,LV_COUNTER) > 0 loop',
'    LV_START_CLEAN  := instr(LV_OUTPUT, ''["''  ,1,LV_COUNTER)+2;',
'    LV_END_CLEAN    := instr(LV_OUTPUT, ''","'' ,1,LV_COUNTER);',
'    LV_LENGTH_CLEAN := LV_END_CLEAN-LV_START_CLEAN;',
'    LV_CLEAN_TEXT := LV_CLEAN_TEXT || substr(LV_OUTPUT, LV_START_CLEAN, LV_LENGTH_CLEAN);',
'    LV_COUNTER := LV_COUNTER + 1;',
'  end loop;',
'  ',
'  -- Create a fake json in the format of the google commercial API result so we can convert the unicode easily via apex_json.get_varchar2',
'  LV_CLEAN_TEXT := ''{"data":{"translations":[{"translatedText":"''||LV_CLEAN_TEXT||''"}]}}'';',
'  ',
'  -- Parse the JSON',
'  apex_json.parse(LV_RESPONSE, LV_CLEAN_TEXT);',
'  -- And return the translatedText value without the unicode',
' return apex_json.get_varchar2(P_PATH=> ''data.translations[%d].translatedText'',P0=> 1, P_VALUES=>LV_RESPONSE);    ',
'',
'end TRS_TRANSLATE_STRING_GOOGLE;',
'/',
