'',
'create or replace package TRANS_APEX',
'is',
'  C_SOURCE_APP          CONSTANT T_TRS_STRING.SOURCE%type := ''APPLICATION'';',
'  C_MAX_LENGTH_TRANS    CONSTANT number := 1000;   ',
'',
'  procedure CREATE_NEW_APP_LANGUAGE         ( IV_APP_ID in number, IV_APP_LANG in varchar2, IV_SEED in number default 0 );',
'  procedure SYNC_STRINGS_TO_LOCAL_REPO      ( IV_PROJECT_ID in number, IV_APP_LANG in varchar2, IV_SEED in number default 0 );',
'  procedure SYNC_DIST_STRINGS_LOCAL_REPO    ( IV_TRANS_ID                    in number, ',
'                                              IV_ONLY_READY_STRINGS          in number, ',
'                                              IV_OVERWRITE_READY_STRINGS     in number,',
'                                              IV_UPDATE_STRINGS_TO_READY     in number );',
'  procedure SYNC_STRINGS_FROM_LOCAL_REPO    ( IV_TRANS_ID in number, IV_ONLY_READY_STRINGS in number, IV_PUBLISH in number );',
'  procedure SEED_PROJECT                    ( IV_PROJECT_ID in number, IV_SYNC_LOCAL_REPO in number );',
'  procedure TRANSLATE_DISTINCT_STRING       ( IV_TRANS_ID in number, IV_READY_NO in number, IV_READY_AUTO in number, IV_READY_YES in number );',
'  procedure START_TRANSLATE_JOB             ( IV_TRANS_ID in number, IV_READY_NO in number, IV_READY_AUTO in number, IV_READY_YES in number );',
'  function  GET_SETTING                     ( IV_KEY in varchar2 ) return varchar2;',
'  procedure DELETE_TRANS                    ( IV_TRANS_ID in number, IV_DELETE_APEX in number );',
'  procedure DELETE_PROJECT                  ( IV_PROJECT_ID in number, IV_DELETE_APEX in number );',
'  procedure CREATE_EXISTING_LANG_FROM_APEX  ( IV_PROJECT_ID in number, IV_APP_ID in number);',
'end TRANS_APEX;',
'/',
'',
'set define off',
'',
'create or replace package body TRANS_APEX',
'is',
'--------------------------------------------------------------------------------',
'  procedure CREATE_NEW_APP_LANGUAGE ( IV_APP_ID in number, IV_APP_LANG in varchar2, IV_SEED in number default 0 ) is',
'    LV_NEX_APP_ID   number;',
'    LV_CREATED      number default 0;',
'    LV_WORKSPACE    varchar2(255);',
'    LV_WORKSPACE_ID number;    ',
'  begin',
'',
'    select WORKSPACE_DISPLAY_NAME INTO LV_WORKSPACE from APEX_APPLICATIONS where APPLICATION_ID=IV_APP_ID;',
'   ',
'    select    WORKSPACE_ID',
'    into      LV_WORKSPACE_ID',
'    from      APEX_WORKSPACES',
'    where     WORKSPACE = LV_WORKSPACE;',
'   ',
'    wwv_flow_api.set_security_group_id(LV_WORKSPACE_ID);',
'  ',
'    select max(APPLICATION_ID) into LV_NEX_APP_ID from APEX_APPLICATIONS;',
'',
'    while (LV_CREATED = 0 ) loop',
'        begin',
'            LV_NEX_APP_ID := LV_NEX_APP_ID + 1;',
'            APEX_LANG.CREATE_LANGUAGE_MAPPING(',
'                P_APPLICATION_ID => IV_APP_ID,',
'                P_LANGUAGE => IV_APP_LANG,',
'                P_TRANSLATION_APPLICATION_ID => LV_NEX_APP_ID  );',
'            LV_CREATED := 1;',
'        exception',
'        when OTHERS then',
'          null;',
'        end;',
'    end loop;',
'',
'    if IV_SEED = 1 then',
'      APEX_LANG.SEED_TRANSLATIONS ( IV_APP_ID, IV_APP_LANG );',
'    end if;',
'',
'end CREATE_NEW_APP_LANGUAGE;',
'--------------------------------------------------------------------------------',
'procedure SYNC_STRINGS_TO_LOCAL_REPO ( IV_PROJECT_ID in number, IV_APP_LANG in varchar2, IV_SEED in number default 0 ) is',
'    LV_APP_ID       number;',
'    LV_TRANS_ID     number;',
'    LV_WORKSPACE    varchar2(255);',
'    LV_WORKSPACE_ID number;    ',
'  begin',
'',
'    select APP_ID into LV_APP_ID from T_TRS_PROJECT where ID=IV_PROJECT_ID;',
'    select ID into LV_TRANS_ID from T_TRS_TRANS where PROJECT_ID=IV_PROJECT_ID and LANGUAGE_CODE=IV_APP_LANG;',
'  ',
'    select WORKSPACE_DISPLAY_NAME INTO LV_WORKSPACE from APEX_APPLICATIONS where APPLICATION_ID=LV_APP_ID;',
'   ',
'    select    WORKSPACE_ID',
'    into      LV_WORKSPACE_ID',
'    from      APEX_WORKSPACES',
'    where     WORKSPACE = LV_WORKSPACE;',
'   ',
'    wwv_flow_api.set_security_group_id(LV_WORKSPACE_ID); ',
'   ',
'    if IV_SEED = 1 then',
'      APEX_LANG.SEED_TRANSLATIONS ( LV_APP_ID, IV_APP_LANG );',
'    end if;',
'',
'    insert into T_TRS_STRING /* Hash value if filled by column default */',
'    (',
'     TRANS_ID ',
'    ,SOURCE ',
'    ,SOURCE_ID',
'    ,SOURCE_TXT ',
'    ,TARGET_TXT ',
'    )  ',
'    select',
'        LV_TRANS_ID,',
'        TRANS_APEX.C_SOURCE_APP,',
'        R.ID,',
'        R.FROM_STRING,',
'        R.TO_STRING',
'    from ',
'        APEX_APPLICATION_TRANS_REPOS R',
'    where ',
'        R.APPLICATION_ID=LV_APP_ID ',
'    and R.LANGUAGE_CODE=IV_APP_LANG',
'    and not exists ( select null from T_TRS_STRING S where S.SOURCE=TRANS_APEX.C_SOURCE_APP and S.SOURCE_ID = R.ID and S.TRANS_ID = LV_TRANS_ID)',
'    and R.FROM_STRING is not null',
'    and R.TRANSLATABLE = ''Y'';',
'    ',
'    delete from T_TRS_STRING S ',
'    where ',
'        S.TRANS_ID = LV_TRANS_ID ',
'    and S.SOURCE=TRANS_APEX.C_SOURCE_APP',
'    and not exists ( select null from APEX_APPLICATION_TRANS_REPOS R where S.SOURCE_ID = R.ID and R.APPLICATION_ID=LV_APP_ID and R.LANGUAGE_CODE=IV_APP_LANG and R.TRANSLATABLE = ''Y'' and R.FROM_STRING is not null);',
'',
'    update T_TRS_STRING S               -- Hash is updated via table default',
'    set S.SOURCE_TXT = ( select R.FROM_STRING ',
'                         from   APEX_APPLICATION_TRANS_REPOS R ',
'                         where  R.APPLICATION_ID    = LV_APP_ID',
'                         and    R.LANGUAGE_CODE     = IV_APP_LANG',
'                         and    R.ID                = S.SOURCE_ID',
'                        )',
'    ,   S.READY = 0             ',
'    where',
'        S.SOURCE    = TRANS_APEX.C_SOURCE_APP ',
'    and S.TRANS_ID  = LV_TRANS_ID;',
'    ',
'    insert into T_TRS_DISTINCT_STRING',
'    (',
'     TRANS_ID ',
'    ,SOURCE_TXT',
'    ,SOURCE_HASH_VAL',
'    ,TARGET_TXT',
'    )',
'    select',
'         LV_TRANS_ID',
'        ,S.SOURCE_TXT ',
'        ,S.SOURCE_HASH_VAL',
'        ,S.TARGET_TXT        ',
'    from ',
'        T_TRS_STRING S',
'    where ',
'        S.TRANS_ID = LV_TRANS_ID',
'    and not exists ( select null from T_TRS_STRING LO where S.TRANS_ID = LO.TRANS_ID and S.ROWID > LO.ROWID and S.SOURCE_HASH_VAL=LO.SOURCE_HASH_VAL )',
'    and not exists ( select null from T_TRS_DISTINCT_STRING LO where S.TRANS_ID = LO.TRANS_ID and S.SOURCE_HASH_VAL=LO.SOURCE_HASH_VAL );',
'    ',
'    delete from T_TRS_DISTINCT_STRING S ',
'    where ',
'        S.TRANS_ID = LV_TRANS_ID ',
'    and not exists ( select null from T_TRS_STRING LO where S.TRANS_ID = LO.TRANS_ID and S.SOURCE_HASH_VAL=LO.SOURCE_HASH_VAL );    ',
'    ',
'     ',
'end SYNC_STRINGS_TO_LOCAL_REPO;',
'--------------------------------------------------------------------------------',
'procedure SYNC_DIST_STRINGS_LOCAL_REPO ( IV_TRANS_ID                    in number, ',
'                                         IV_ONLY_READY_STRINGS          in number, ',
'                                         IV_OVERWRITE_READY_STRINGS     in number,',
'                                         IV_UPDATE_STRINGS_TO_READY     in number ) is',
'',
'  begin ',
'  ',
'    update T_TRS_STRING S',
'    set    S.TARGET_TXT = ( select LO.TARGET_TXT from T_TRS_DISTINCT_STRING LO where LO.TRANS_ID = IV_TRANS_ID and LO.SOURCE_HASH_VAL = S.SOURCE_HASH_VAL ),',
'           S.READY      = case when IV_UPDATE_STRINGS_TO_READY = 1 then 1 else S.READY end',
'    where ',
'           S.READY = case when IV_OVERWRITE_READY_STRINGS = 1 then S.READY else 0 end',
'    and    S.TRANS_ID = IV_TRANS_ID',
'    and    S.SOURCE_HASH_VAL = case when IV_ONLY_READY_STRINGS = 1 ',
'                               then ( select LO.SOURCE_HASH_VAL ',
'                                      from T_TRS_DISTINCT_STRING LO ',
'                                      where LO.TRANS_ID = S.TRANS_ID ',
'                                      and   S.SOURCE_HASH_VAL = LO.SOURCE_HASH_VAL ',
'                                      and   LO.READY = 1 )',
'                               else S.SOURCE_HASH_VAL end;',
'    ',
'end SYNC_DIST_STRINGS_LOCAL_REPO;',
'--------------------------------------------------------------------------------',
'procedure SYNC_STRINGS_FROM_LOCAL_REPO ( IV_TRANS_ID in number, IV_ONLY_READY_STRINGS in number, IV_PUBLISH in number ) is',
'    LV_APP_ID   T_TRS_PROJECT.APP_ID%type;',
'    LV_APP_LANG T_TRS_TRANS.LANGUAGE_CODE%type;',
'    LV_WORKSPACE    varchar2(255);',
'    LV_WORKSPACE_ID number;    ',
'  begin',
'  ',
'    select P.APP_ID into LV_APP_ID from T_TRS_PROJECT P join T_TRS_TRANS T on ( P.ID = T.PROJECT_ID) where T.ID = IV_TRANS_ID;',
'    select LANGUAGE_CODE into LV_APP_LANG from T_TRS_TRANS where ID = IV_TRANS_ID;',
'    ',
'    select WORKSPACE_DISPLAY_NAME INTO LV_WORKSPACE from APEX_APPLICATIONS where APPLICATION_ID=LV_APP_ID;',
'   ',
'    select    WORKSPACE_ID',
'    into      LV_WORKSPACE_ID',
'    from      APEX_WORKSPACES',
'    where     WORKSPACE = LV_WORKSPACE;',
'   ',
'    wwv_flow_api.set_security_group_id(LV_WORKSPACE_ID);    ',
'    ',
' ',
'    for LV_TRS_STRINGS in ( select  SOURCE_ID, TARGET_TXT ',
'                            from    T_TRS_STRING S ',
'                            where   S.READY = case when IV_ONLY_READY_STRINGS = 1 then 1 else S.READY end',
'                            and     S.SOURCE = TRANS_APEX.C_SOURCE_APP ) loop',
'        APEX_LANG.UPDATE_TRANSLATED_STRING ( LV_TRS_STRINGS.SOURCE_ID, LV_APP_LANG, LV_TRS_STRINGS.TARGET_TXT );',
'    end loop;',
'',
'    if IV_PUBLISH = 1 then',
'        APEX_LANG.PUBLISH_APPLICATION ( LV_APP_ID, LV_APP_LANG );',
'    end if;',
'  ',
'end SYNC_STRINGS_FROM_LOCAL_REPO;',
'--------------------------------------------------------------------------------',
'procedure SEED_PROJECT ( IV_PROJECT_ID in number, IV_SYNC_LOCAL_REPO in number ) is',
'    LV_APP_ID   T_TRS_PROJECT.APP_ID%type;',
'',
'  begin',
'  ',
'    select P.APP_ID into LV_APP_ID from T_TRS_PROJECT P where P.ID = IV_PROJECT_ID;',
' ',
'    for LV_APP_LANG in ( select LANGUAGE_CODE from T_TRS_TRANS where PROJECT_ID = IV_PROJECT_ID ) loop',
'  ',
'        if IV_SYNC_LOCAL_REPO = 1 then',
'            TRANS_APEX.SYNC_STRINGS_TO_LOCAL_REPO ( IV_PROJECT_ID, LV_APP_LANG.LANGUAGE_CODE, 1 );',
'        end if;',
'',
'    end loop;',
'  ',
'end SEED_PROJECT;',
'',
'--------------------------------------------------------------------------------',
'',
'procedure TRANSLATE_DISTINCT_STRING ( IV_TRANS_ID in number, IV_READY_NO in number, IV_READY_AUTO in number, IV_READY_YES in number ) is',
'  LV_APP_LANG       T_TRS_TRANS.LANGUAGE_CODE%type;',
'  LV_APP_ORIG_LANG  T_TRS_TRANS.LANGUAGE_CODE%type;',
'  LV_APP_ID         T_TRS_PROJECT.APP_ID%type;',
'  LV_REGEXP         T_TRS_SETTING.VALUE%type;',
'begin',
'',
'select P.APP_ID                     into LV_APP_ID          from T_TRS_PROJECT P join T_TRS_TRANS T on ( P.ID = T.PROJECT_ID) where T.ID = IV_TRANS_ID;',
'select LANGUAGE_CODE                into LV_APP_LANG        from T_TRS_TRANS where ID = IV_TRANS_ID;',
'select APPLICATION_PRIMARY_LANGUAGE into LV_APP_ORIG_LANG   from APEX_APPLICATIONS where application_id = LV_APP_ID;',
'',
'LV_REGEXP := TRANS_APEX.GET_SETTING(''REGEXP_INSTR_SKIP_DISTINCT_STRING_SOURCE_TXT'');',
'',
'for LV_STRING in ( select ID from T_TRS_DISTINCT_STRING where TRANS_ID = IV_TRANS_ID',
'                                                          and READY in ( decode(IV_READY_NO,1,0,null), decode(IV_READY_AUTO,1,2,null), decode(IV_READY_YES,1,1,null) )',
'                                                          and regexp_instr(SOURCE_TXT,LV_REGEXP) = 0',
'                                                          order by ID ) loop ',
'    begin',
'        update T_TRS_DISTINCT_STRING',
'          set ',
'            TARGET_TXT = TRS_TRANSLATE_STRING(SOURCE_TXT, LV_APP_ORIG_LANG, LV_APP_LANG),',
'            READY      = 2',
'        where',
'            ID = LV_STRING.ID; ',
'        commit;',
'    exception',
'        when OTHERS then',
'            null;',
'    end;    ',
'end loop;',
'',
'end TRANSLATE_DISTINCT_STRING;',
'--------------------------------------------------------------------------------',
'',
'procedure START_TRANSLATE_JOB ( IV_TRANS_ID in number, IV_READY_NO in number, IV_READY_AUTO in number, IV_READY_YES in number )',
'is',
'  LV_JOB_NAME CONSTANT varchar2(30) := ''TRANS_APEX_''||IV_TRANS_ID;',
'  cursor C_JOB_EXISTS',
'  is',
'  select 1 ',
'  from user_scheduler_jobs',
'  where job_name = LV_JOB_NAME;',
'',
'  LV_JOB    varchar2(100);',
'  LV_EXISTS number(1) := 0;',
'',
'begin',
'',
'  open  C_JOB_EXISTS;',
'  fetch C_JOB_EXISTS into LV_EXISTS;',
'  close C_JOB_EXISTS;',
'  ',
'  if LV_EXISTS = 1 ',
'  then',
'    dbms_scheduler.drop_job(LV_JOB_NAME,true);',
'  end if;',
'',
'',
'  LV_JOB :=   ''begin TRANS_APEX.TRANSLATE_DISTINCT_STRING(''''''||IV_TRANS_ID||'''''',''''''||IV_READY_NO||'''''',''''''||IV_READY_AUTO||'''''',''''''||IV_READY_YES||''''''); end; '';',
'',
'  dbms_scheduler.create_job(',
'      job_name => LV_JOB_NAME',
'     ,job_type => ''PLSQL_BLOCK''',
'     ,job_action => LV_JOB',
'     ,start_date => SYSTIMESTAMP',
'     ,enabled => TRUE',
'     ,auto_drop => FALSE',
'     ,comments => ''TRANS APEX translation job for TRANS_ID ''||IV_TRANS_ID);',
'',
'end START_TRANSLATE_JOB;',
'--------------------------------------------------------------------------------',
'',
'function GET_SETTING ( IV_KEY in varchar2 ) return varchar2',
'is',
'  LV_VALUE  T_TRS_SETTING.VALUE%type;',
