'    TRANS_APEX.SYNC_STRINGS_TO_LOCAL_REPO ( IV_PROJECT_ID, EXISTING_LANG.TRANSLATED_APP_LANGUAGE,   1 );  -- Create local repo records, seed first.',
'',
'  end loop;',
'',
